import React, { useState } from "react";
import TodoContainer from "./components/organisms/TodoContainer";

const App: React.FC = () => {
  const [todos, setTodos] = useState<
    Array<{ content: string; complete: boolean; dueDate?: string }>
  >([]);

  const addTodo = (content: string, dueDate?: string) => {
    setTodos([...todos, { content, complete: false, dueDate }]);
  };

  const toggleComplete = (index: number) => {
    const newTodos = [...todos];
    newTodos[index].complete = !newTodos[index].complete;
    setTodos(newTodos);
  };

  const deleteTodo = (index: number) => {
    setTodos(todos.filter((_, i) => i !== index));
  };

  const clearAll = () => {
    setTodos([]);
  };

  return (
    <div>
      <TodoContainer
        todos={todos}
        setTodos={setTodos}
        addTodo={addTodo}
        toggleComplete={toggleComplete}
        deleteTodo={deleteTodo}
        clearAll={clearAll}
      />
    </div>
  );
};

export default App;
