// src/components/atoms/TodoItem.tsx
import React from "react";
import styles from "./TodoItem.module.css";

interface TodoItemProps {
  todo: { content: string; complete: boolean; dueDate?: string };
  toggleComplete: () => void;
  deleteTodo: () => void;
}

const TodoItem: React.FC<TodoItemProps> = ({
  todo,
  toggleComplete,
  deleteTodo,
}) => {
  return (
    <li
      className={todo.complete ? styles.complete : ""}
      onDoubleClick={deleteTodo}
    >
      <button className={styles.button} onClick={toggleComplete}></button>
      <span>{todo.content}</span>
      {todo.dueDate && <span> | {todo.dueDate}</span>}
    </li>
  );
};

export default TodoItem;
