// src/components/atoms/InputField.tsx
import React from "react";
import styles from "./InputField.module.css";

// onKeyPress prop 추가 및 타입 정의 수정
const InputField: React.FC<{
  value: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  placeholder?: string;
  onKeyPress?: (e: React.KeyboardEvent<HTMLInputElement>) => void; // onKeyPress prop 추가
}> = ({ value, onChange, placeholder, onKeyPress }) => {
  return (
    <input
      className={styles.input}
      type="text"
      value={value}
      onChange={onChange}
      placeholder={placeholder}
      onKeyPress={onKeyPress} // onKeyPress 이벤트 핸들러 추가
    />
  );
};

export default InputField;
