import React, { useState } from "react";
import styles from "./TodoItem.module.css";

interface Todo {
  id: string;
  content: string;
  complete: boolean;
  dueDate?: string;
}

interface TodoItemProps {
  todo: Todo;
  toggleComplete: () => void;
  deleteTodo: () => void;
  editTodo: (content: string, dueDate?: string) => void;
  dragHandleProps?: any;
}

const TodoItem: React.FC<TodoItemProps> = ({
  todo,
  toggleComplete,
  deleteTodo,
  editTodo,
  dragHandleProps,
}) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editContent, setEditContent] = useState(todo.content);
  const [editDueDate, setEditDueDate] = useState(todo.dueDate || "");

  const handleEdit = () => {
    editTodo(editContent, editDueDate || undefined);
    setIsEditing(false);
  };

  const getDueDateColor = () => {
    if (!todo.dueDate) return "";

    const today = new Date();
    const dueDate = new Date(todo.dueDate);
    const timeDiff = dueDate.getTime() - today.getTime();
    const daysDiff = Math.ceil(timeDiff / (1000 * 3600 * 24));

    if (daysDiff < 0) return "red"; // 지난 날짜
    if (daysDiff <= 1) return "#ff9800"; // 오늘/내일
    return "#f1b116"; // 여유 있음
  };

  return (
    <li
      className={`${styles.todoItem} ${todo.complete ? styles.complete : ""}`}
    >
      {isEditing ? (
        <>
          <input
            type="text"
            value={editContent}
            onChange={(e) => setEditContent(e.target.value)}
            className={styles.editInput}
          />
          <input
            type="date"
            value={editDueDate}
            onChange={(e) => setEditDueDate(e.target.value)}
            className={styles.editDateInput}
          />
          <button onClick={handleEdit} className={styles.editButton}>
            저장
          </button>
          <button
            onClick={() => setIsEditing(false)}
            className={styles.editButton}
          >
            취소
          </button>
        </>
      ) : (
        <>
          <input
            type="checkbox"
            checked={todo.complete}
            onChange={toggleComplete}
            className={styles.checkbox}
          />
          <span>{todo.content}</span>
          {todo.dueDate && <span> | {todo.dueDate}</span>}
          <button className={styles.editButton} onClick={deleteTodo}>
            ✕
          </button>
        </>
      )}
    </li>
  );
};

export default TodoItem;
