import React, { useState, useEffect } from "react";
import TodoContainer from "./components/organisms/TodoContainer";

const App: React.FC = () => {
  // Define the todo items state
  const [todos, setTodos] = useState<
    { content: string; dueDate?: string; complete: boolean }[]
  >([]);

  // Load the todo items from local storage
  useEffect(() => {
    const savedTodos = JSON.parse(localStorage.getItem("saved-items") || "[]");
    setTodos(savedTodos);
  }, []);

  // Save the todo items to local storage
  useEffect(() => {
    localStorage.setItem("saved-items", JSON.stringify(todos));
  }, [todos]);

  // Add a new todo item
  const addTodo = (content: string, dueDate?: string) => {
    setTodos([...todos, { content, dueDate, complete: false }]);
  };

  // Toggle the complete status of a todo item
  const toggleComplete = (index: number) => {
    const newTodos = [...todos];
    newTodos[index].complete = !newTodos[index].complete;
    setTodos(newTodos);
  };

  // Delete
  const deleteTodo = (index: number) => {
    const newTodos = todos.filter((_, i) => i !== index);
    setTodos(newTodos);
  };

  // Clear all
  const clearAll = () => {
    setTodos([]);
  };

  return (
    <TodoContainer
      todos={todos}
      addTodo={addTodo}
      toggleComplete={toggleComplete}
      deleteTodo={deleteTodo}
      clearAll={clearAll}
    />
  );
};

export default App;
