import React from "react";
import TodoInput from "../molecules/TodoInput";
import TodoList from "./TodoList";
import Button from "../atoms/Button";
import styles from "./TodoContainer.module.css";

const TodoContainer: React.FC<{
  todos: any[];
  addTodo: (content: string, dueDate?: string) => void;
  toggleComplete: (index: number) => void;
  deleteTodo: (index: number) => void;
  clearAll: () => void;
}> = ({ todos, addTodo, toggleComplete, deleteTodo, clearAll }) => {
  return (
    <div className={styles.todoContainer}>
      <h1>To Do</h1>
      <TodoInput addTodo={addTodo} />
      <TodoList
        todos={todos}
        toggleComplete={toggleComplete}
        deleteTodo={deleteTodo}
      />
      <div className={styles.deleteBtnWrap}>
        <button className={styles.deleteBtn} onClick={clearAll}>
          Clear All
        </button>
      </div>
    </div>
  );
};

export default TodoContainer;
